remediationSample:
let f = 0;
let success = false; // a variable to return the status

try {
    f = open(filename);
    text = f.read();
    success = true;
}
catch(err) {
    logger.error("Cannot read", filename, err);
    success = false;
}
finally {
    if (f) {
        f.close();
    }
    return success; 
}

sample:
let f = 0;
try {
    f = open(filename);
    text = f.read();
    return true;
}
catch(err) {
    logger.error("Cannot read", filename, err);
    return false; // Overwritten by finally
}
finally {
    if (f) {
        f.close();
    }
    return true; 
}
