var jqxhr = $.ajax( "example.php" )
  .done(function() {
    alert( "success" );
  })
  .fail(function() {
    alert( "error" );
  })
  .always(function() {
    alert( "complete" );
  });

// OR 

// Set another completion function for the request above
jqxhr.always(function() {
  alert( "second complete" );
});
