remediationSample:
// with helmet 

var express = require('express'); 
var app = express(); 
var helmet = require('helmet'); 

app.use(helmet.hsts()); 

app.get('/', function(req, res){ 
    res.send("<html><body><p>hello world</p><script type='text/javascript'>alert('got you')</script></body><html>"); 
}); 

app.listen(3000);

