Oracle:
DECLARE
    pe_ratio NUMBER(3,1);
    BEGIN
        BEGIN  ---------- sub-block begins
            SELECT 1 / NVL(null, 0) INTO pe_ratio FROM dual;
        EXCEPTION
          WHEN ZERO_DIVIDE THEN
            -- Compliant
            pe_ratio := 0;
        END;  ---------- sub-block ends
    EXCEPTION
        WHEN OTHERS THEN
            NULL;
            -- Compliant
            pe_ratio := Null;
    END;
