DSS_METRIC_DESCRIPTIONS.METRIC_DESCRIPTION:
SELECT first_name, department_name
FROM employees
     NATURAL JOIN departments;

Could be rewritten as: SELECT first_name,department_name FROM employees JOIN departments ON (employees.manager_id = departments.manager_id AND employees.department_id = departments.department_id);
or as:
SELECT first_name, department_name FROM employees JOIN departments USING(manager_id, department_id);

The following statement should be reviewed:
DECLARE
  "End" NUMBER := 1;
BEGIN
  dbms_output.put_line('A quoted identifier End ['||"End"||']');
END;
/

The following statement should be reviewed:
DECLARE
  L_End NUMBER := 1;
BEGIN
  dbms_output.put_line('A quoted identifier End ['||L_End||']');
END;
/

The following procedure should be reviewed:
CREATE PROCEDURE SEARCH (IN V_DYNAMICSQL VARCHAR(2000))
BEGIN
  EXECUTE IMMEDIATE V_DYNAMICSQL;
END;

The following procedure should be reviewed:
CREATE PROCEDURE INSERT_VALUES
BEGIN
  INSERT INTO Persons VALUES ('Last', 'First', 'Add', 'City');
END;

Add columns in INSERT clause:
CREATE PROCEDURE INSERT_VALUES
BEGIN
  INSERT INTO Persons (LastName, FirstName, Address, City) VALUES ('Last', 'First', 'Add', 'City');
END;

Microsoft SQL Server: BEGIN TRY -- Generate divide-by-zero error. SELECT 1/0 END TRY BEGIN CATCH -- Non-compliant END CATCH;
MySQL and MariaDB: DECLARE CONTINUE HANDLER FOR SQLWARNING # Non-compliant BEGIN # empty block END;
PostgreSQL: BEGIN INSERT INTO db(a,b) VALUES (key, data); RETURN; EXCEPTION WHEN unique_violation THEN -- Do nothing, and loop to try the UPDATE again. NULL; END;
Oracle: DECLARE pe_ratio NUMBER(3,1); BEGIN BEGIN ---------- sub-block begins SELECT 1 / NVL(null, 0) INTO pe_ratio FROM dual; EXCEPTION WHEN ZERO_DIVIDE THEN -- Compliant pe_ratio := 0; END; ---------- sub-block ends EXCEPTION WHEN OTHERS THEN -- Non-Compliant NULL; END;

Microsoft SQL Server:
BEGIN TRY  
-- Generate divide-by-zero error. 
    SELECT 1/0 
END TRY 
BEGIN CATCH  
-- Compliant
 SELECT ERROR_NUMBER() AS ErrorNumber, ERROR_MESSAGE() AS ErrorMessage;
END CATCH; 

MySQL and MariaDB:
DECLARE CONTINUE HANDLER FOR SQLWARNING 
# Compliant
BEGIN 
# not an empty block
SET done = TRUE;
END;

PostgreSQL:
BEGIN
    INSERT INTO db(a,b) VALUES (key, data);
        RETURN;
    EXCEPTION WHEN unique_violation THEN
    RAISE NOTICE 'caught unique_violation ';
    RETURN;
END;

Oracle:
DECLARE
    pe_ratio NUMBER(3,1);
    BEGIN
        BEGIN  ---------- sub-block begins
            SELECT 1 / NVL(null, 0) INTO pe_ratio FROM dual;
        EXCEPTION
          WHEN ZERO_DIVIDE THEN
            -- Compliant
            pe_ratio := 0;
        END;  ---------- sub-block ends
    EXCEPTION
        WHEN OTHERS THEN
            NULL;
            -- Compliant
            pe_ratio := Null;
    END;
